import { createContext, useState } from "react";

export const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);

  console.log("CAAART", cart);
  const addCart = (item) => {
    setCart([
      ...cart,
      { ...item, id: Math.floor(Date.now() * Math.random()).toString(36) },
    ]);
    localStorage.setItem("@ContextCart", JSON.stringify(cart));
  };

  const removeCart = (item) => {
    const newListCart = cart.filter((element) => element.name !== item.name);
    setCart(newListCart);
  };

  return (
    <CartContext.Provider value={{ cart, setCart, addCart, removeCart }}>
      {children}
    </CartContext.Provider>
  );
};
